import React, { Component } from 'react';
import apiHandler from '../../api/apiHandler';
import UserContext from "../Auth/UserContext";
import { buildFormData } from "../../utils";

class FormEditAnnounce extends Component {
    static contextType = UserContext;
    state = {
        httpResponse: null,
    };

    formRef = React.createRef();

    handleChange = (event) => {
        const key = event.target.name;
        const value = 
        event.target.type === "file" ? event.target.files[0] : event.target.value;
        this.setState({
          [key]: value,
        });
    };


    handleSubmit = (event) => {
        event.preventDefault();
        const fd = new FormData();
        const { httpResponse, ...data } = this.state;
        buildFormData(fd, data); 
        apiHandler
          .updateAnnounce(this.props.announce._id, fd)
          .then((data) => {
            //clear form
            this.props.onAnnounceUpdate(data);
            this.setState({
              httpResponse: {
                status: "success",
                message: "Report successfully added.",
              },
            });
            this.timeoutId = setTimeout(() => {
              this.setState({ httpResponse: null });
            }, 1000);
          })
          .catch((error) => {
            this.setState({
              httpResponse: {
                status: "failure",
                message: "An error occured, try again later.",
              },
            });
            this.timeoutId = setTimeout(() => {
              this.setState({ httpResponse: null });
            }, 1000);
          });
      };


    handleFileSelect = ({ tmpUrl, file }) => {
            this.setState({ image: file });
    };

    render() {
    const {
      title,
      name,
      location,
      email,
      description,
      image,
      pet_type,
      comments
    } = this.props.announce;

        return (
            <div className="ItemForm-container">
            <form 
            onSubmit={this.handleSubmit}
            ref={this.formRef}
            onChange={this.handleChange}
            className="ItemForm"
            >
            <p onClick={this.props.handleClose} className="close-link">
            X
          </p>
          <h2>Edit Announce</h2>
                <div>
            <label htmlFor="title">Title</label>
            <input
              onChange={this.handleChange}
              value={this.props.title}
              id="title"
              name="title"
              type="text"
            />
                </div>
          <div>
          <label htmlFor="name">Name</label>
            <input
              onChange={this.handleChange}
              value={name}
              id="name"
              name="name"
              type="text"
            />
          </div>
            <div>
            <label htmlFor="location">Location</label>
            <input
              onChange={this.handleChange}
              value={location}
              id="location"
              name="location"
              type="text"
            />
            </div>
            <div>
            <label htmlFor="email">E-mail</label>
            <input
              onChange={this.handleChange}
              value={email}
              id="email"
              name="email"
              type="text"
            />
            </div>
            <div>
            <label htmlFor="description">Description</label>
            <input
              onChange={this.handleChange}
              value={description}
              id="description"
              name="description"
              type="text"
            />
            </div>

            <div>
            <label htmlFor="image">Image</label>
            <input
              ref={this.imageRef}
              onChange={this.handleChange}
              value={image}
              id="image"
              name="image"
              type="file"
            />
            </div>


            <div>
            <label htmlFor="pet_type">Pet type</label>
            <input
              onChange={this.handleChange}
              value={pet_type}
              id="pet_type"
              name="pet_type"
              type="text"
            />
            </div>

           <div>
           <label htmlFor="comments">Comments</label>
            <input
              onChange={this.handleChange}
              value={comments}
              id="comments"
              name="comments"
              type="text"
            />
           </div>

            <button>Submit !</button>
          </form>
        </div>)
    };
};

export default FormEditAnnounce;
