import React from "react";
import withUser from "../components/Auth/withUser";
import { NavLink } from "react-router-dom";
import UserContext from "../components/Auth/UserContext";
import apiHandler from "../api/apiHandler";
import FormEditAnnounce from "../components/Forms/FormEditAnnounce"

class Profile extends Component {
  static contextType = UserContext;

  state = {
    phone_number: "",
    userAnnounce: [],
    httpResponse: null,
    isLoading: true,
  };

  componentDidMount() {
    apiHandler.getUserAnnounce().then((data) => {
      this.setState({ userAnnounce: data });
    });
  }

  submitPhoneNumber = (event) => {
    event.preventDefault();
    const { httpResponse, userItems, selectedItem, ...userData } = this.state;
    apiHandler
      .updateUser(userData)
      .then((data) => {
        this.context.setUser(data);
        this.setState({
          httpResponse: { status: "success", message: "Phone number added." },
        });
        this.timeoutId = setTimeout(() => {
          this.setState({ httpResponse: null });
        }, 1000);
      })
      .catch((error) => {
        this.setState({
          httpResponse: {
            status: "failure",
            message: "An error occured, try again later",
          },
        });
        this.timeoutId = setTimeout(() => {
          this.setState({ httpResponse: null });
        }, 1000);
      });
  };

  handlePhoneNumber = (event) => {
    this.setState({ phone_number: event.target.value });
  };

  deleteAnnounce = (announceId) => {
    apiHandler.removeAnnounce(announceId).then(() => {
      const userAnnounces = [...this.state.userAnnounces].filter(
        (announce) =>  announce._id !== announceId
      );
      this.setState({ userAnnounces });
    });
  };

  handleAnnounceUpdate = (updatedAnnounce) => {
    const userAnnounces = [...this.state.userAnnounces].map((announce) =>
    announce._id === updatedAnnounce._id ? updatedAnnounce : announce
    );
    this.setState({ userAnnounces });
  };

  addAnnounce = (announce) => {
    this.setState({ userAnnounces: [...this.state.userAnnounces, announce] });
  };

  componentWillUnmount() {
    clearTimeout(this.timeoutId);
  }



  render() {
    return (
      <section className="Profile">
        {selectedItem && (
          <FormEditAnnounce
            item={selectedItem}
            handleClose={this.onEditFormClose}
            onItemUpdate={this.handleItemUpdate}
          />
        )}
        {user && this.props.displayForm && (
          <ItemForm
            handleClose={this.props.handleFormClose}
            addItem={this.addItem}
          />
        )}
        <div className="user-image round-image">
          <img src={user.profileImg} alt={user.firstName} />
        </div>
        <div className="user-presentation">
          <h2 className="title">
            {user.firstName} {user.lastName}
          </h2>
          <Link className="link" to="/profile/settings">
            Edit profile
          </Link>
        </div>

        <div className="user-contact">
          <h4>Add a phone number</h4>
          {httpResponse && (
            <FeedBack
              message={httpResponse.message}
              status={httpResponse.status}
            />
          )}
          <form
            className="form"
            onChange={this.handlePhoneNumber}
            onSubmit={this.submitPhoneNumber}
          >
            <div className="form-group">
              <label className="label" htmlFor="phoneNumber">
                Phone number
              </label>
              <input
                className="input"
                id="phoneNumber"
                type="text"
                name="phoneNumber"
                defaultValue={this.context.user.phoneNumber || ""}
                placeholder="Add phone number"
              />
            </div>
            <Button primary>Add phone number</Button>
          </form>
        </div>

        {!userItems.length && (
          <React.Fragment>
            <div className="">
              <img src="/media/personal-page-empty-state.svg" alt="" />
            </div>
            <p>You don't have any items :(</p>
          </React.Fragment>
        )}

        {!!userItems.length && (
          <div className="CardItems">
            <h3>Your items</h3>
            {userItems.map((item, index) => (
              <CardItem
                key={index}
                {...item}
                handleDelete={this.deleteItem}
                handleEdit={this.onItemSelect}
              />
            ))}
          </div>
        )}
      </section>
    );
  }
}

export default withUser(Profile);
