import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import UserContext from "../components/Auth/UserContext";
import apiHandler from "../api/apiHandler";
import FormEditAnnounce from "../components/Forms/FormEditAnnounce";
import { Link } from "react-router-dom";

class Profile extends Component {
  static contextType = UserContext;

  state = {
   userAnnounce:[],
  };

  componentDidMount() {
    apiHandler
    .getUserAnnounce()
    .then((data) => {
      this.setState({ userAnnounce: data });
    });
  }

  deleteAnnounce = (announceId) => {
    apiHandler.removeAnnounce(announceId).then(() => {
      const userAnnounces = [...this.state.userAnnounces].filter(
        (announce) =>  announce._id !== announceId
      );
      this.setState({ userAnnounces });
    });
  };



  render() {
    return (

      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Image</th>
            <th>Update</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {this.state.userAnnounce.map((announce) => {
            return (
              <tr key={announce._id}>
                <td>{announce.title}</td>
                <td>
                  <img
                    style={{ width: 48, height: 48 }}
                    src={announce.image}
                    alt={announce.name}
                  />
                </td>
                <td>
                  <FormEditAnnounce to={`/announce/${announce._id}/edit`}/>
                    <button>Edit</button>
                </td>
                <td>
                  <button onClick={() => this.deleteAnnounce(announce._id)}>
                    Delete
                  </button>
                  <button><NavLink to="/profile">Missing</NavLink></button>
                </td>
              </tr>
            );

          })}
        </tbody>
      </table>
    );
  }
}



export default Profile;
