import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import UserContext from "../components/Auth/UserContext";
import apiHandler from "../api/apiHandler";
import FormEditAnnounce from "../components/Forms/FormEditAnnounce";
import { Link } from "react-router-dom";
import FormAnnouncement from "../components/Forms/FormAnnouncement";
import CardAnnounce from "../components/CardAnnounce"

class Profile extends Component {
  static contextType = UserContext;

  state = {
    phoneNumber: "",
    httpResponse: null,
    userAnnounces:[],
    selectedAnnounce: null,
  };

  componentDidMount() {
    apiHandler
    .getUserAnnounce()
    .then((data) => {
      this.setState({ userAnnounces: data });
    });
  }

  submitPhoneNumber = (event) => {
    event.preventDefault(); // Disable the forms default behaviour (refresh the page and make request.)
    const { httpResponse, userAnnounces, selectedAnnounce, ...userData } = this.state;
    apiHandler
      .updateUser(userData)
      .then((data) => {
        this.context.setUser(data);
        this.setState({
          httpResponse: { status: "success", message: "Phone number added." },
        });
        this.timeoutId = setTimeout(() => {
          this.setState({ httpResponse: null });
        }, 1000);
      })
      .catch((error) => {
        this.setState({
          httpResponse: {
            status: "failure",
            message: "An error occured, try again later",
          },
        });
        this.timeoutId = setTimeout(() => {
          this.setState({ httpResponse: null });
        }, 1000);
      });
  };

  handlePhoneNumber = (event) => {
    this.setState({ phoneNumber: event.target.value });
  };



  deleteAnnounce = (announceId) => {
    apiHandler.removeAnnounce(announceId).then(() => {
      const userAnnounces = [...this.state.userAnnounces].filter(
        (announce) =>  announce._id !== announceId
      );
      this.setState({ userAnnounces });
    });
  };

  onAnnounceSelect = (announceId) => {
    const selectedAnnounce = this.state.userAnnounces.find(
      (announce) => announce._id === announceId
    );
    this.setState({ selectedAnnounce: selectedAnnounce });
  };

  onEditFormClose = () => {
    this.setState({ selectedAnnounce: null });
  };

  handleAnnounceUpdate = (updatedAnnounce) => {
    const userAnnounces = [...this.state.userAnnounces].map((announce) =>
    announce._id === updatedAnnounce._id ? updatedAnnounce : announce
    );
    this.setState({ userAnnounces });
  };

  addAnnounce = (announce) => {
    this.setState({ userAnnounces: [...this.state.userAnnounces, announce] });
  };

  render() {
    const { user } = this.context;
    const { userAnnounces, selectedAnnounce } = this.state;
    if (!user) return null;

    return (
      <section className="Profile">

        {selectedAnnounce && (
          <FormEditAnnounce
            item={selectedAnnounce}
            handleClose={this.onEditFormClose}
            onItemUpdate={this.handleAnnounceUpdate}
          />
        )}
        {user && this.props.displayForm && (
          <FormAnnouncement
            handleClose={this.props.handleFormClose}
            addAnnounce={this.addAnnounce}
          />
        )}
        <div className="user-presentation">
          <h2 className="title">
            {user.username}
          </h2>
          <button><NavLink to="/create">Report Missing</NavLink></button>

          <button><Link className="link" to="/profile/settings">Edit profile</Link></button>
        </div>

        <div className="user-contact">
          <h4>Add a phone number</h4>
          {httpResponse && (
            <FeedBack
              message={httpResponse.message}
              status={httpResponse.status}
            />
          )}
          <form
            className="form"
            onChange={this.handlePhoneNumber}
            onSubmit={this.submitPhoneNumber}
          >
            <div className="form-group">
              <label className="label" htmlFor="phoneNumber">
                Phone number
              </label>
              <input
                className="input"
                id="phoneNumber"
                type="text"
                name="phoneNumber"
                defaultValue={this.context.user.phoneNumber || ""}
                placeholder="Add phone number"
              />
            </div>
            <Button primary>Add phone number</Button>
          </form>
        </div>

        {!userAnnounces.length && (
          <React.Fragment>
            <div className="">
              <img src="{}" alt="" />
            </div>
            <p>You don't have any  :(</p>
          </React.Fragment>
        )}

        {!!userAnnounces.length && (
          <div className="CardAnnounce">
            <h3>Your announcements</h3>
            {this.state.userAnnounces.map((announce, index) => (
              <CardAnnounce
                key={index}
                {...announce}
                handleDelete={this.deleteAnnounce}
                handleEdit={this.onAnnounceSelect}
              />
            ))}
          </div>
        )}
      </section>
    );



  }
}



export default Profile;
