import React, { Component } from "react";
import Button from "../../Button";
import apiHandler from "../../api/apiHandler";
import UserContext from "../Auth/UserContext";
buildFormData(fd, data);


class FormProfile extends Component {
  static contextType = UserContext;
  state = {
    user: null,
    tmpUrl: "",
    httpResponse: null,
    isLoading: true,
  };

  imageRef = React.createRef();

  componentDidMount() {
    const userId = this.props.match.params.id;
    apiHandler
      .getUserInfos(userId)
      .then((data) => {
        this.setState({ user: data, isLoading: false });
      })
      .catch((error) => {
        this.setState({
          isLoading: false,
          httpResponse: {
            status: "failure",
            message: "Something bad happened, please try again later",
          },
        });
      });
  }

  handleChange = (event) => {
    const key = event.target.name; // This function requires that you have the "name" attribute on your form fields.
    const value = event.target.value;
    this.setState({ user: { ...this.state.user, [key]: value } });
  };

  isValidInput = (key) => {
    if (this.state.user[key] === "") {
      return false;
    } else return true;
  };

  checkError = () => {
    for (const key in this.state.user) {
      if (this.state[key] === "") {
        return true;
      }
    }
    return false;
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const fd = new FormData();
    buildFormData(fd, data)
    fd.append("profileImg", this.imageRef.current.files[0]);

    const userId = this.props.match.params.id;

    apiHandler
      .updateUser(fd, userId)
      .then((data) => {
        this.context.setUser(data);
        this.setState({
          httpResponse: {
            status: "success",
            message: "Profile successfully updated.",
          },
        });

        this.timeoutId = setTimeout(() => {
          this.setState({ httpResponse: null });
        }, 2000);
      })
      .catch((error) => {
        this.setState({
          httpResponse: {
            status: "failure",
            message:
              "Something bad happened while updating your profile, try again later",
          },
        });

        this.timeoutId = setTimeout(() => {
          this.setState({ httpResponse: null });
        }, 2000);
      });
  };

  render() {
    // const { user } = this.state;
    const { httpResponse } = this.state;

    if (this.state.isLoading) return <div>Loading...</div>;

    return (
        <div className="missing">

        <form autoComplete="off" className="form" onSubmit={this.handleSubmit}>
          <h1 className="header">Edit profile</h1>
          <div>
            <label htmlFor="profilImg"></label>
            <img
              src={this.state.tmpUrl || this.state.user.profileImg}
              alt={this.state.user.profilImg}
            />
            <input
              ref={this.imageRef}
              onChange={this.handleChange}
              value={this.state.user.profilImg}
              id="profilImg"
              name="profilImg"
              type="file"
            />
            </div>


          <div className="form-group">
            <label className="label" htmlFor="username">
              Username
            </label>
            <input
              className="input"
              id="username"
              type="text"
              name="username"
              onChange={this.handleChange}
              value={this.state.user.username}

            />
            {!this.isValidInput("username") && (
              <p className="input-error">Invalid input</p>
            )}
          </div>

          <div className="form-group">
            <label className="label" htmlFor="password">
              Password
            </label>
            <input
              className="input"
              id="password"
              type="text"
              name="password"
              onChange={this.handleChange}
              value={this.state.user.password}

            />
            {!this.isValidInput("password") && (
              <p className="input-error">Invalid input</p>
            )}
          </div>
          <div className="form-group">
            <label className="label" htmlFor="phoneNumber">
              Phone number
            </label>
            <input
              className="input"
              id="phoneNumber"
              type="text"
              name="phoneNumber"
              onChange={this.handleChange}
              value={this.state.user.phoneNumber}
            />
            {!this.isValidInput("phoneNumber") && (
              <p className="input-error">Invalid input</p>
            )}
          </div>
          <button>Save</button>
        </form>

      </div>
    );
  }
}

export default FormProfile;
