import React, { Component } from 'react'
import apiHandler from "../api/apiHandler"
import { Link } from "react-router-dom";

class Missing extends Component {
    state = {
    announce: [],
    }

    componentDidMount() {
        apiHandler
        .getAnnounce()
       .then((responseFromApi) => {
          console.log(responseFromApi);
          this.setState({
            announce: responseFromApi.data,
          });
        });
      }


    render() {
        return (
            <div>
                <h2>Missing page</h2>

          {this.state.announce.map((ann) => {
            return (
              <div key={ann._id}>
                <img
                  style={{
                    width: 80,
                    height: 100,
                  }}
                  src={ann.image}
                  alt={ann.name}
                />
                <Link to={`/api/announce/${announce._id}`}>
                  <h2>{ann.title}</h2>
                </Link>
                <h3>{ann.name}</h3>
                <h3> {ann.location}</h3>
                <h3> {ann.email}</h3>
                <h3> {ann.pet_type}</h3>
                <h3> {ann.description}</h3>

              </div>
            );
          })}
            </div>
        )
    }
}

export default Missing
