import React, { Component } from 'react';
import apiHandler from '../../api/apiHandler';
import UserContext from "../Auth/UserContext";
import { buildFormData } from "../../utils";


class FormEditAnnounce extends Component {
    state = {
        title: "",
        name: "",
        location: "",
        email: "",
        description: "",
        image:"",
        missing: true,
        pet_type:"",
        comments:"",
    };

    formRef = React.createRef();

    componentDidMount() {
        const announceId = this.props.match.params.id;

        apiHandler
          .getOneAnnounce(announceId)
          .then((apiResponse) => {
            this.setState({
               announce: apiResponse
            });
          })
          .catch((error) => {
            console.log(error);
          });
      }


  handleChange = (event) => {
    const value =
      event.target.type === "file" ? event.target.files[0] : event.target.value;
    const key = event.target.name;
    this.setState({ [key]: value });
  };


    handleSubmit = (event) => {
        event.preventDefault();

        const fd = new FormData();
        const { httpResponse, ...data } = this.state;

        fd.append("image", this.imageRef.current.files[0]);

        apiHandler
          .updateAnnounce(this.props.announce._id, fd)
          .then((data) => {
            console.log(this.props)
            this.props.onAnnounceUpdate(data);
            this.props.history.push("/profile");
            this.setState({
                httpResponse: {
                  status: "success",
                  message: "Item successfully added.",
                },
              });
              this.timeoutId = setTimeout(() => {
                this.setState({ httpResponse: null });
              }, 1000);
            })
            .catch((error) => {
              this.setState({
                httpResponse: {
                  status: "failure",
                  message: "An error occured, try again later.",
                },
              });
              this.timeoutId = setTimeout(() => {
                this.setState({ httpResponse: null });
              }, 1000);
            });

      };

      handleFileSelect = ({ tmpUrl, file }) => {
        this.setState({ image: file });
      };


    render() {
        return (
            <form onSubmit={this.handleSubmit}
            ref={this.formRef}>
                <div>
                <label htmlFor="title">Title</label>
            <input
              onChange={this.handleChange}
              value={this.state.title}
              id="title"
              name="title"
              type="text"
            />
                </div>
          <div>
          <label htmlFor="name">Name</label>
            <input
              onChange={this.handleChange}
              value={this.state.name}
              id="name"
              name="name"
              type="text"
            />
          </div>
            <div>
            <label htmlFor="location">Location</label>
            <input
              onChange={this.handleChange}
              value={this.state.location}
              id="location"
              name="location"
              type="text"
            />
            </div>
            <div>
            <label htmlFor="email">E-mail</label>
            <input
              onChange={this.handleChange}
              value={this.state.email}
              id="email"
              name="email"
              type="text"
            />
            </div>
            <div>
            <label htmlFor="description">Description</label>
            <input
              onChange={this.handleChange}
              value={this.state.description}
              id="description"
              name="description"
              type="text"
            />
            </div>

            <div>
            <label htmlFor="image">Image</label>
            <input
              onFileSelect={this.handleFileSelect}
              ref={this.imageRef}
              value={this.state.image}
              id="image"
              name="image"
              type="file"
            />
            </div>


            <div>
            <label htmlFor="pet_type">Pet type</label>
            <input
              onChange={this.handleChange}
              value={this.state.pet_type}
              id="pet_type"
              name="pet_type"
              type="text"
            />
            </div>

           <div>
           <label htmlFor="comments">Comments</label>
            <input
              onChange={this.handleChange}
              value={this.state.comments}
              id="comments"
              name="comments"
              type="text"
            />
           </div>

            <button>Submit !</button>
          </form>
        );
    };
};

export default FormEditAnnounce;
