import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import UserContext from "../components/Auth/UserContext";
import apiHandler from "../api/apiHandler";
import FormEditAnnounce from "../components/Forms/FormEditAnnounce";
import { Link } from "react-router-dom";

class Profile extends Component {
  static contextType = UserContext;

  state = {
   userAnnounce:[],
  };

  componentDidMount() {
    apiHandler
    .getUserAnnounce()
    .then((data) => {
      this.setState({ userAnnounce: data });
    });
  }

  deleteAnnounce = (announceId) => {
    apiHandler.removeAnnounce(announceId).then(() => {
      const userAnnounces = [...this.state.userAnnounces].filter(
        (announce) =>  announce._id !== announceId
      );
      this.setState({ userAnnounces });
    });
  };



  render() {
    return (
      <div className="missing">
            <h2 className="missing_title">Profile page</h2>
             {this.state.userAnnounce && this.state.userAnnounce.map((announce)=>{
                if (announce.missing){
                    return (
                        <div className="missing_info" key={announce._id}>
                            <img
                          style={{
                            width: "100vw",
                            height: "50vh",
                          }}
                          src={announce.image}
                          alt={announce.name}
                            />
                        <Link to={`/missing/${announce._id}`}>
                          <h2>{announce.title}</h2>
                        </Link>
                        <h3>{announce.name}</h3>
                        <h3>{announce.location}</h3>
                        <h3>{announce.pet_type}</h3>
                        <h3>{announce.description}</h3>

                      </div>
                )}
            })}
       </div>
   )    
  }
}



export default Profile;
