import React, { Component } from 'react'
import apiHandler from "../api/apiHandler"
import { Link } from "react-router-dom";

class Missing extends Component {
    state = {
        announces: [],
    };

    componentDidMount() {

        apiHandler
        .getAnnounce()
        .then( function (responseFromApi){
            if (this.state.announces.missing === "true")
          this.setState({
            announces: responseFromApi
          });
        })
        .catch((error) => {
            console.log(error);
        });
    }


    render() {
        return (
            <div>
            <h2>Missing page</h2>
             {this.state.announces  && this.state.announces.map((announce) => {
                return (
                <div key={announce._id}>
                    <img
                  style={{
                    width: 80,
                    height: 100,
                  }}
                  src={announce.image}
                  alt={announce.name}
                    />
                <Link to={`/missing/${announce._id}`}>
                  <h2>{announce.title}</h2>
                </Link>
                <h3>{announce.name}</h3>
                <h3>{announce.location}</h3>
                <h3>{announce.pet_type}</h3>
                <h3>{announce.description}</h3>

              </div>
            );
            })}
        </div>
        )
    }
}

export default Missing
