import React, { Component } from 'react';
import { withRouter } from "react-router-dom";
import apiHandler from '../../api/apiHandler';

class FormAnnouncement extends Component {
    state = {
        title: "",
        name: "",
        location: "",
        email: "",
        image: "",
        description: "",
        pet_type: "",
        missing: true,
        comments: "",
    };

    handleChange = (event) => {
        const { name, value } = event.target;
        this.setState({
          [name]: value,
        });
    };

    handleSubmit = (event) => {
        event.preventDefault();
        apiHandler
          .post(data, {
            title: this.state.title,
            name: this.state.name,
            location: this.state.location,
            email: this.state.email,
            image: this.state.image,
            description: this.state.description,
            pet_type:this.state.pet_type,
            missing:this.state.missing,
            comments:this.state.comments,
          })
          .then((apiResponse) => {
            this.props.history.push("/missing");
            // console.log("Created !");
            // console.log(apiResponse);
          });
      };

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
            <label htmlFor="title">Title</label>
            <input
              onChange={this.handleChange}
              value={this.state.title}
              id="title"
              name="title"
              type="text"
            />
            <label htmlFor="name">Name</label>
            <input
              onChange={this.handleChange}
              value={this.state.name}
              id="name"
              name="name"
              type="text"
            />
            <label htmlFor="location">Location</label>
            <input
              onChange={this.handleChange}
              value={this.state.location}
              id="location"
              name="location"
              type="text"
            />
            <label htmlFor="email">E-mail</label>
            <input
              onChange={this.handleChange}
              value={this.state.first_brewed}
              id="email"
              name="email"
              type="text"
            />
            <label htmlFor="description">Description</label>
            <input
              onChange={this.handleChange}
              value={this.state.description}
              id="description"
              name="description"
              type="text"
            />

            <label htmlFor="image">Image</label>
            <input
              onChange={this.handleChange}
              value={this.state.image}
              id="image"
              name="image"
              type="text"
            />
            <label htmlFor="pet_type">Pet type</label>
            <input
              onChange={this.handleChange}
              value={this.state.pet_type}
              id="pet_type"
              name="pet_type"
              type="text"
            />
            <label htmlFor="missing">Missing</label>
            <input
              onChange={this.handleChange}
              value={this.state.missing}
              id="missing"
              name="missing"
              type="text"
            />
             <label htmlFor="comments">Comments</label>
            <input
              onChange={this.handleChange}
              value={this.state.comments}
              id="comments"
              name="comments"
              type="text"
            />
            <button>Submit !</button>
          </form>
        );
    }
}

export default FormAnnouncement;
